insert  into `wp_posts`(`ID`,`post_author`,`post_date`,`post_date_gmt`,`post_content`,`post_title`,`post_excerpt`,`post_status`,`comment_status`,`ping_status`,`post_password`,`post_name`,`to_ping`,`pinged`,`post_modified`,`post_modified_gmt`,`post_content_filtered`,`post_parent`,`guid`,`menu_order`,`post_type`,`post_mime_type`,`comment_count`) values (267,1,'2011-06-25 14:22:31','2011-06-25 14:22:31','<img class=\"alignleft size-full wp-image-192\" title=\"local-tab-img\" src=\"http://www.clusterstogo.com/wp-content/uploads/2011/06/local-tab-img.png\" alt=\"\" width=\"200\" height=\"264\" />\r\nAs we launch our services, we are proud to be starting off by serving the Bay Area exclusively.\r\n\r\nOur scanning facilities are based right here in the Bay Area. We are not like the other “cheap” scanning services where they ship off your pictures to “distant” lands. <em>And we all know the horror stories about what happens when things cross the borders!</em>\r\n\r\nThus, be assured that you are helping the local communities as you use our services, are employing local people, and doing your part in cranking up the wheels of economy of our good ol’ California!','Local tab title here','','inherit','open','open','','189-revision-11','','','2011-06-25 14:22:31','2011-06-25 14:22:31','',189,'http://www.clusterstogo.com/189-revision-11/',0,'revision','',0);
